import axios from "axios";
import React, {useState} from "react";
import ShowAllEmployee from "./ShowAllEmployee";
export default function AssignProjects() {
  // creating state variables
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  // creating onchange events

  const onTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const onDescChange = (event) => {
    setDesc(event.target.value);
  };
  const [users, setUsers] = useState(null);
  // using useEffect react hook
  axios.get("http://localhost:5000/user/getUsers").then((result) => {
    setUsers(result.data.users);
  });
  console.log("demo");

  return (
    <div>
      <h3>Create Project</h3>
      <br />
      <form method="post">
        <label htmlFor="title">Project Title: </label>
        <input
          type="text"
          name="title"
          value={title}
          onChange={onTitleChange}
        />
        <br />
        <label htmlFor="desc">Project Description: </label>
        <input type="text" name="desc" value={desc} onChange={onDescChange} />
      </form>
      <br />
      <br />
      <br />

      <label htmlFor="all">All Employees</label>
      {
        users ? users.map((value, index ) => {
          
          {/* <ShowAllEmployee user={value} key={index} /> */}
          <div key={index}>
            <h3>{value.name}</h3>
            <h3>{value.email}</h3>
          </div>
        }) :
        <div>No data found</div>
      }
    </div>
  );
}
