import React from 'react'
import ProjectMembers from './ProjectMembers'

export default function ShowSingleProject({project, setTitle, setDesc, setHead, setMembers}) {
    
  return (
    <div>
        <label htmlFor="fhs">Title: </label>
        <input type="text" value={project.name} onChange={(event) => {
            setTitle(event.target.value)
        }}/>
        <br />
        <label htmlFor="fhs">Description: </label>
        <input type="text" value={project.desc} onChang={(event)=> {
            setDesc(event.target.value)
        }}  />
        <br />
        <label htmlFor="fhs">Project Head: </label>
        <input type="text" value={project.head} onChange={(event) => setHead(event.target.value)} />
        <br />
        <label htmlFor="fhs">Status: </label>
        <input type="text" value={project.status} onChange={(event) => setHead(event.target.value)} />
        <br />

        <h2>Project Members</h2>
        {
            project.members ? project.members.map((user) => {
                return <ProjectMembers user = {user} setMembers={setMembers} />
            })
            :
            <div>No members are allocated</div>
        }
        
    </div>
  )
}
