import axios from "axios";
import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Alert from './Alert'
export default function AddTask({ setClicked, project }) {
  const [task, setTask] = useState("");
  const user = useSelector((state) => state.userObj.userObj);
  const [msg, setMsg] = useState(null);
  
  const onFormSubmit = async () => {
    const url = "http://localhost:5000/user/save-tasks"
    const data = {
        userID : user._id,
        projectID : project._id,
        tasks: task
    }
    const response = await axios.post(url, data)
    console.log("response data0 ", response.data);
    if(response.data.status !== "success") {
        setMsg(response.data.msg)
    }
  };
  return (
    <div>
      <h3>Create task</h3>
      {
          msg ? <Alert msg={msg} alertColor={"red"} /> : <div></div>
      }
      <form onSubmit={onFormSubmit}>
        <textarea
          name="task"
          value={task}
          cols="30"
          rows="10"
          onChange={(event) => {
            setTask(event.target.value);
          }}
          className="
        form-control
        block
        w-full
        px-3
        py-1.5
        text-base
        font-normal
        text-gray-700
        bg-white bg-clip-padding
        border border-solid border-gray-300
        rounded
        transition
        ease-in-out
        m-0
        focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none
      "
        ></textarea>
        <br />
        <input
          type="submit"
          value="Add task"
          onClick={() => {
            setClicked(false);
          }}
        />
      </form>
    </div>
  );
}
