import React, {useState} from 'react'
import axios from 'axios'
import { Navigate } from "react-router-dom";

// Register component
export default function Register() {

    // declaring variables to store user info
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [mobile, setMobile] = useState(3);

    // collecting user info
    const changeName = (event) => {
        setName(event.target.value)
    }
    const changeEmail = (event) => {
        setEmail(event.target.value)
    }
    const changeMobile = (event) => {
        setMobile(event.target.value)
    }
    const changePassword = (event) => {
        setPassword(event.target.value)
    }

    // defining what would happen after form submission
    const onFormSubmit = async (event) => {
        event.preventDefault()
        const response = await axios.post("http://localhost:5000/user/register")
        if (response.data.user) {
            return <Navigate to="/login" />
        } 
        else{
            return <Navigate to="/register" />
        }
        
    }

    // rendering JSX
  return (
    <div>
        <h1>Register</h1>
        <form method="post" onSubmit={onFormSubmit}>
            <input type="text" name='name' required placeholder='enter name' onChange={changeName}/>
            <input type="email" name='email' required placeholder='enter email' onChange={changeEmail}/>
            <input type="number" name='mobile' required placeholder='enter mobile no' onChange={changeMobile} />
            <input type="password" name='password' required onChange={changePassword} />
            <input type="submit" value="Register" />
        </form>
    </div>
  )
}
