import React, { useState } from "react";

export default function ShowAllEmployee({ user, getSelectedUsers }) {
  const ImagePath = "http://localhost:5000/profile-pic/";
  console.log("in child");
  const [arrayOfSelectedUsers, setArrayOfSelectedUsers] = useState([]);

  const [checked, setChecked] = React.useState(false);

  const handleChange = () => {
    console.log("initial value is ", checked);
    setChecked(!checked);
    console.log("final check i s ", checked);
    if (checked) {
        if(!arrayOfSelectedUsers.includes(user._id)) {
            arrayOfSelectedUsers.push(user._id)
        }
    } else {
        if(arrayOfSelectedUsers.includes(user._id)) {
            let index = arrayOfSelectedUsers.findIndex(user._id)
            delete arrayOfSelectedUsers[index]
        }
    }
  };
  console.log("value of check is ", checked);
  return (
    <div>
      <div key={user._id}>
        {user.pic ? (
          <img
            src={ImagePath + user.pic}
            alt="loading"
            height="50"
            width="50"
          />
        ) : (
          <div>not uploaded</div>
        )}
        <input type="text" value={user.name} readOnly />
        <input type="text" value={user.email} readOnly />
        <input type="checkbox" checked={checked} onChange={handleChange} />
      </div>
    </div>
  );
}
