import axios from 'axios';
import React from 'react'
import { useState, useEffect } from 'react';
export default function AssignProjects() {

    // creating state variables
    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");

    // creating onchange events

    const onTitleChange = (event) => {
        setTitle(event.target.value)
    }

    const onDescChange = (event) => {
        setDesc(event.target.value)
    }

    // using useEffect react hook
    let allUsers = null;
    useEffect( () => {
        axios.post("http://localhost:5000/user/getUsers").then(result => {
            allUsers = result.data;
        })
       
    });
    console.log("all users are ", allUsers);

  return (
    <div>
        <h3>Create Project</h3>
        <br />
        <form method="post">
            <label htmlFor="title">Project Title:  </label>
            <input type="text" name='title' value={title} onChange={onTitleChange} /> 
            <br />
            <label htmlFor="desc">Project Description:  </label>
            <input type="text" name='desc' value={desc} onChange={onDescChange} />
        </form>
        <br />
        <br />
        <br />
        <label htmlFor="all">All Employees</label>
    </div>
  )
}
