import React, {useState} from 'react'
import axios from 'axios'
export default function Login() {

    
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const onEmailChange = (event) => {
        setEmail(event.target.value)
    }
    const onPasswordChange = (event) => {
        setPassword(event.target.value)
    }
    const onFormSubmit = async (event) => {
        event.preventDefault();
        const user = {
            email:email,
            password: password
        }
        const response = await axios.post("http://localhost:5000/user/register", user);
        if(response.data){
            console.log("data after login is ", response.data);
        }
    }

    return (
    <>
        <h1>Login</h1>
        <form method='post' onSubmit={onFormSubmit}>
            <input type="email" name='email' placeholder='enter Email' onChange={onEmailChange} />
            <input type="password" name='password' onChange={onPasswordChange} />
            <input type="submit" value='Login' />
        </form>
    </>
    )
}
