import axios from "axios";
import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useLocation } from "react-router-dom";
import Alert from "./Alert";

export default function ShowTasks({task}) {
  const user = useSelector((state) => state.userObj.userObj);
  const location = useLocation();
  const { project } = location.state;
  const [allTasks, setAllTasks] = useState(null);
  const [msg, setMsg] = useState(null);
  console.log("task is in show task ", task);
  useEffect(() => {
    const getTasks = async () => {
      const url =
        "http://localhost:5000/user/get-tasks/?userID=" +
        user._id +
        "&projectID=" +
        project._id +
        "&position=" +
        user.position;
      const response = await axios.get(url);
      if (response.data.status === "success") {
        console.log(" show tasks all tasks are ", response.data.task);
        setAllTasks({projectID: project._id, tasks: [...response.data.task.tasks]});
      } else {
        console.log("all tasks are ", response.data.task);
        setMsg(response.data.msg);
      }
    };
    getTasks();
  }, []);

  return (
    <div>
      <div>
        {msg ? <Alert /> : <div></div>}
        <h2>priority : {task.priority} || task: {task.task} || status: {task.status} </h2>
      </div>
    </div>
  );
}
