import axios from 'axios';
import React, {useState} from 'react'
import { useSelector, useDispatch } from "react-redux";
import {  Navigate, useNavigate, Link } from 'react-router-dom';
import {changeLoginState} from '../store/userLoginSlice'
import {updateUser} from '../store/userObjSlice'
export default function UserProfile() {

    const dispatch = useDispatch() // creating object of dispatch using useDispatch()
    const navigate = useNavigate() // creating object of navigate using useNavigate()
    const user = useSelector((state) => state.userObj.userObj)
    const currentLoginStatus = useSelector((state) => state.userLoginInfo.isLoggedIn)
    console.log("current login status is ", currentLoginStatus);
    const logOut = () => {
        dispatch(changeLoginState(false))
        dispatch(updateUser({}))
        navigate("/register")
    }
    // creating variables to store the personal info of users
    let [name, setName] = useState(user.name);
    let [email, setEmail] = useState(user.email);
    let [mobile, setMobile] = useState(user.mobile);
    console.log("name is ", name);
    const onNameChange = (event) => {
        setName(event.target.value)
    }
    const onEmailChange = (event) => {
        setEmail(event.target.value)
    }
    const onMobileChange = (event) => {
        setMobile(event.target.value)
    }
    const formSubmit = async (event) => {
        event.preventDefault();
        if(name && email && mobile) {
            name = name.trim();
            email = email.trim();
            const newUser = {
                name: name,
                email: email,
                mobile: mobile,
                userEmail: user.email
            }
            const response = await axios.post("http://localhost:5000/user/edit-profile/",newUser);
            dispatch(updateUser(response.data))
            navigate("/user-home")
        }

    }

    
    
    return (
    <div>
        Welcome {user.name} <br />
        <Link to="/user-home">Home</Link>
        <button onClick={logOut}>Log Out</button> 
        <br />
        <h1>Edit section</h1>
        <form onSubmit={formSubmit} method="post">
            <input type="text" name="name" value={name} onChange={onNameChange} /> <br />
            <input type="email" name="email" value={email} onChange={onEmailChange} /> <br />
            <input type="number" name="mobile" value={mobile} onChange={onMobileChange} /> <br />
            <input type="submit" value="Save Changes" />
        </form>

    </div>
    
    )
}
