import React, {useState} from 'react'

export default function AddTask() {
    const [task, setTask] = useState("");
  return (
    <div>
        <textarea name="task" value={task}  cols="30" rows="10" onChange={(event) => {
            setTask(event.target.value)
        }} ></textarea>
    </div>
  )
}
