import axios from "axios";
import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Alert from "./Alert";
import {updateTasks} from "../store/taskSlice"

export default function AddTask({ setClicked, project }) {
  const [task, setTask] = useState("");
  const user = useSelector((state) => state.userObj.userObj);
  const [msg, setMsg] = useState(null);
  const dispatch = useDispatch()

  const [selectedOption, setSelectedOption] = useState({priority: "high"});
  const handleOptionChange = (event) => {
    console.log("changed value", event.target.value);
    setSelectedOption({priority: event.target.value})
  }
  console.log("default option ", selectedOption);
  const options = [{label: "High Priority", value: "high"},{label: "Medium Priority", value: "medium"},{label: "Low Priority", value: "low"} ]

  const onFormSubmit = async (e) => {
    e.preventDefault();
    if(task.length <=0) {
      return
    }
    setClicked(false)
    const url = "http://localhost:5000/user/save-tasks";
    const data = {
      userID: user._id,
      projectID: project._id,
      tasks: selectedOption,
      position : user.position
    };
    const response = await axios.post(url, data);
    console.log("response data0 ", response.data);
    if (response.data.status !== "success") {
      setMsg(response.data.msg);
      // dispatch(updateTasks())
    } else {
      dispatch(updateTasks(response.data.tasks.tasks))
    }
  };
  return (
    <div>
      <h3>Create task</h3>
      {msg ? <Alert msg={msg} alertColor={"red"} /> : <div></div>}
      <form onSubmit={onFormSubmit} method="post">

      <select  onChange={handleOptionChange}>
        {options.map((option) => (
          <option value={option.value}>{option.label}</option>
        ))}
      </select>

        <textarea
          name="task"
          value={task}
          cols="30"
          rows="10"
          onChange={(event) => {
            setTask(event.target.value);
          }}
          className="
        form-control
        block
        w-full
        px-3
        py-1.5
        text-base
        font-normal
        text-gray-700
        bg-white bg-clip-padding
        border border-solid border-gray-300
        rounded
        transition
        ease-in-out
        m-0
        focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none
      "
        ></textarea>
        <br />
        <input
          type="submit"
          value="Add task"
          
        />
      </form>
    </div>
  );
}
