import { Link } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import axios from "axios";
import ShowAllProjectsOfUser from "./ShowAllProjectsOfUser";

export default function UserHome() {
  const [user, setUser] = useState(useSelector((state) => state.userObj.userObj));
  // const user = useSelector((state) => state.userObj.userObj)
  const [allProjects, setAllProjects] = useState(null);
  useEffect(() => {
    // setUser(useSelector((state) => state.userObj.userObj))
    alert(user.email)
    console.log("user email is ", user);
    let demo = {
      ...user
    }
    
    axios.get("http://localhost:5000/user/get-all-projects", user ).then((result) => {
    console.log(result.data.msg);  
    if(result.data.status === "success") {
        setAllProjects(result.data.allProjects);
        console.log("data of projects fetched successfully");
        console.log(result.data);
      } else {
        console.log("failed to get data of projects");
      }
    });
  }, []);
  // alert(user)
  console.log("user is user", user);
  console.log("all projects", allProjects);
  return (
    <div>
      <label htmlFor="ha">Home Page</label>
      <br />
      <Link to="/user-profile">Profile</Link>
      <br />
      {user.position === "admin" ? (
        <Link to="/create-project">Create Project</Link>
      ) : (
        <div></div>
      )}
      <h3>All Projects</h3>
      {
        allProjects ? allProjects.map((value) => {
          return < ShowAllProjectsOfUser project={value} />
        }) : <div>No project is assigned yet</div>
      }
    </div>
  );
}
