import React from 'react'
import { useLocation } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';
import ShowProjects from './ShowProjects';
export default function ShowProjectStatus() {

    const [users, setUsers] = useState(null);
    const [allProjects, setAllProjects] = useState(null);

    const location = useLocation();
    useEffect(() => {
        const getAllProjects = async () => {
            const {status} = location.state
            console.log("status is ", status);
            const response = axios.get("/get-projects-for-updation/?status=" + status)
            if((await response).data.status === "success") {
                setAllProjects((await response).data.allProjects)
            }
        }

        getAllProjects()
        const getAllUsers = async () => {
            const users = await axios.get("http://localhost:5000/user/getUsers")
            if(users.data) {
                if(users.data.status === "success") {
                    setUsers(users.data.users)
                }
            }
        }
        getAllUsers()
        
    }, []);

    
  return (
    <div>
        <div>
            {
                allProjects? allProjects.map((project) => {
                    return <ShowProjects project={project} users={users} />
                }) :
                <div>No projects found</div>
            }
        </div>

    </div>
  )
}
