import React from "react";
import { Link } from "react-router-dom";
import ProjectMembers from "./ProjectMembers";
import { useLocation } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
export default function ShowSingleProject() {
  // accessing the value from link component using useLocation hook
  const location = useLocation();
  const { project } = location.state;
  const [msg, setMsg] = useState(null);
  const [title, setTitle] = useState(project.name);
  const [desc, setDesc] = useState(project.desc);
  const [head, setHead] = useState(project.head);
  const [members, setMembers] = useState(project.members);
  const [status, setStatus] = useState(project.status);
  let totalMembers = [];

  for (let index = 0; index < members.length; index++) {
    totalMembers.push(members[index]._id);
  }
  console.log("current members are ", totalMembers);

  const onFormSubmit = async (event) => {

    const currentMembers = []
    for (let index = 0; index < totalMembers.length; index++) {
        if(totalMembers[index]) {
            currentMembers.push(totalMembers[index])
        }
    }
    console.log("in form all members ", totalMembers);
    event.preventDefault();
    const newProject = {
        _id: project._id,
      name: title,
      desc,
      head,
      members: currentMembers,
      status
    };

    // saving all the data 
    const response = axios.post("http://localhost:5000/user/update-project", newProject)
    if(response.data) {
        setMsg("success")
    } else {
        setMsg("failed")
    }

  };

  return (
    <div>
    <h1>{msg} </h1>
      <Link to="/user-home">Home</Link>
      <br />
      <label htmlFor="fhs">Title: </label>
      <form onSubmit={onFormSubmit}>
        <input
          type="text"
          value={title}
          onChange={(event) => {
            setTitle(event.target.value);
          }}
        />
        <br />
        <label htmlFor="fhs">Description: </label>
        <input
          type="text"
          value={desc}
          onChange={(event) => {
            setDesc(event.target.value);
          }}
        />
        <br />
        <label htmlFor="fhs">Project Head: </label>
        <input
          type="text"
          value={head}
          onChange={(event) => setHead(event.target.value)}
        />
        <br />
        <label htmlFor="fhs">Status: </label>
        <input
          type="text"
          value={status}
          onChange={(event) => setStatus(event.target.value)}
        />
        <br />

        <h2>Project Members</h2>
        {members ? (
          members.map((user) => {
            return <ProjectMembers user={user} setMembers={totalMembers} />;
          })
        ) : (
          <div>No members are allocated</div>
        )}
        <br />
        <br />
        <input type="submit" value="update project" />
      </form>
    </div>
  );
}
