import React, {useState} from 'react'
import axios from 'axios'
import { useSelector, useDispatch } from "react-redux";
import {changeLoginState} from '../store/userLoginSlice'
import {updateUser} from '../store/userObjSlice'
import { Navigate, useNavigate } from 'react-router-dom';

export default function Login() {

    const dispatch = useDispatch()
    const navigate = useNavigate()
    const currentLoginStatus = useSelector((state) => state.userLoginInfo.isLoggedIn)
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const onEmailChange = (event) => {
        setEmail(event.target.value)
    }
    const onPasswordChange = (event) => {
        setPassword(event.target.value)
    }
    const onFormSubmit = async (event) => {
        event.preventDefault();
        const user = {
            email:email,
            password: password
        }
        const response = await axios.post("http://localhost:5000/user/login", user);
        dispatch(changeLoginState(true));
        dispatch(updateUser(response.data.user))
        if(response.data){
            console.log("data after login is ", response.data.user);
            navigate("/user-home")
        }
    }
    // if(currentLoginStatus) {
    //     console.log("login is success");
    //     return <Navigate to="/profile" replace={true} />
    // }



    // using "navigate(path)" i can directly navigate to other paths without using return statements and from any function.
    // so i can navigate from any function to any page without using return statement.

    return (
    <div className='h-screen flex bg-gray-100'>
        <div className='w-full max-w-md m-auto bg-white rounded-lg border border-primaryBorder shadow-default py-10 px-16'>
            <h1 className='text-2xl font-medium text-primary mt-4 mb-12 text-center'>Login üîê</h1>
            <form method='post' onSubmit={onFormSubmit} >
                <input type="email" name='email' placeholder='enter Email' onChange={onEmailChange} className={`w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4`} />
                <input type="password" name='password' onChange={onPasswordChange} className={`w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4`}/>
                <div className='flex justify-center items-center mt-6'>
                    <input type="submit" value='Login' className={`bg-emerald py-2 px-4 text-sm text-emerald rounded border border-emerald focus:outline-none focus:border-green-dark`} />
                </div>
            </form>
        </div>
    </div>
    )
}
